/* Ana stil */
body {
    background-color: #f8fafc;
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, sans-serif;
    color: #1a1a1a;
}

/* Başlıklar */
h3, h4 {
    color: #2d3748;
    font-weight: 600;
    margin: 30px 0;
    text-align: center;
}

h3 {
    font-size: 28px;
    background: linear-gradient(135deg, #6366f1 0%, #4f46e5 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
}

/* Form grupları */
.form-group {
    background: white;
    padding: 20px;
    border-radius: 15px;
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);
    margin: 20px 0;
    display: flex;
    align-items: center;
    gap: 20px;
    transition: transform 0.2s ease;
}

.form-group:hover {
    transform: translateY(-2px);
}

.form-group label {
    min-width: 150px;
    color: #4a5568;
    font-weight: 500;
    font-size: 15px;
}

/* Select ve Input alanları */
select, input[type="number"] {
    flex: 1;
    padding: 12px 15px;
    border: 2px solid #e2e8f0;
    border-radius: 10px;
    font-size: 14px;
    color: #2d3748;
    background-color: white;
    transition: all 0.3s ease;
}

select {
    cursor: pointer;
    background-image: url("data:image/svg+xml,...");
    background-repeat: no-repeat;
    background-position: right 10px center;
    background-size: 20px;
    padding-right: 40px;
}

select:focus, input[type="number"]:focus {
    border-color: #6366f1;
    box-shadow: 0 0 0 3px rgba(99, 102, 241, 0.2);
    outline: none;
}

/* Tablo Stili */
.table {
    width: 100%;
    border-spacing: 0 15px;
    border-collapse: separate;
    margin: 25px 0;
}

.table thead tr {
    background: linear-gradient(135deg, #6366f1 0%, #4f46e5 100%);
}

.table th {
    padding: 18px 25px;
    color: white;
    font-weight: 600;
    font-size: 14px;
    text-transform: uppercase;
    letter-spacing: 1px;
}

.table th:first-child {
    border-radius: 12px 0 0 12px;
}

.table th:last-child {
    border-radius: 0 12px 12px 0;
}

.table tbody tr {
    background: white;
    box-shadow: 0 2px 15px rgba(0, 0, 0, 0.08);
    transition: transform 0.2s ease, box-shadow 0.2s ease;
}

.table tbody tr:hover {
    transform: translateY(-3px);
    box-shadow: 0 5px 20px rgba(0, 0, 0, 0.12);
}

.table td {
    padding: 20px 25px;
    background: white;
}

.table td:first-child {
    border-radius: 12px 0 0 12px;
}

.table td:last-child {
    border-radius: 0 12px 12px 0;
}

/* Checkbox stilleri */
.table input[type="checkbox"] {
    width: 18px;
    height: 18px;
    margin-right: 8px;
    border: 2px solid #e2e8f0;
    border-radius: 5px;
    cursor: pointer;
    transition: all 0.2s ease;
}

.table input[type="checkbox"]:checked {
    background: #6366f1;
    border-color: #6366f1;
}

/* Butonlar */
button {
    background: linear-gradient(135deg, #6366f1 0%, #4f46e5 100%);
    color: white;
    padding: 14px 28px;
    border: none;
    border-radius: 10px;
    font-size: 15px;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
    box-shadow: 0 4px 15px rgba(99, 102, 241, 0.3);
    margin: 20px 0;
}

button:hover {
    transform: translateY(-2px);
    box-shadow: 0 6px 20px rgba(99, 102, 241, 0.4);
}

button:active {
    transform: translateY(0);
}

/* Yükleniyor mesajı */
p {
    text-align: center;
    color: #6366f1;
    font-size: 16px;
    font-weight: 500;
    padding: 20px;
    background: rgba(99, 102, 241, 0.1);
    border-radius: 10px;
    margin: 20px 0;
}

/* Not input alanları */
.table input[type="number"] {
    width: 80px;
    padding: 8px;
    text-align: center;
    margin: 0 5px;
}

/* Öğrenim çıktıları checkbox container */
.table td label {
    display: inline-flex;
    align-items: center;
    margin: 5px 15px 5px 0;
    font-size: 14px;
    color: #4a5568;
}
