.login-container {
    display: flex;
    justify-content: center;
    align-items: center;
    min-height: 100vh;
    background: linear-gradient(135deg, #f8fafc 0%, #edf2f7 100%);
    padding: 20px;
}

.login-card {
    background: white;
    padding: 30px;
    border-radius: 15px;
    box-shadow: 0 4px 20px rgba(0, 0, 0, 0.08);
    max-width: 400px;
    width: 100%;
    transition: transform 0.2s ease;
}

.login-card:hover {
    transform: translateY(-2px);
    box-shadow: 0 6px 25px rgba(0, 0, 0, 0.12);
}

.login-header {
    text-align: center;
    margin-bottom: 25px;
}

.uni-logo {
    max-width: 120px;
    height: auto;
    margin-bottom: 15px;
}

.login-title {
    font-size: 24px;
    font-weight: 600;
    margin-bottom: 10px;
    background: linear-gradient(135deg, #6366f1 0%, #4f46e5 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
}

.login-subtitle {
    color: #4a5568;
    font-size: 16px;
    margin-bottom: 25px;
    text-align: center;
}

.form-group {
    margin-bottom: 20px;
}

.form-group label {
    display: block;
    color: #4a5568;
    font-weight: 500;
    margin-bottom: 8px;
    font-size: 14px;
}

.form-control {
    width: 100%;
    padding: 12px 15px;
    border: 2px solid #e2e8f0;
    border-radius: 10px;
    font-size: 14px;
    transition: all 0.3s ease;
}

.form-control:focus {
    border-color: #6366f1;
    box-shadow: 0 0 0 3px rgba(99, 102, 241, 0.2);
    outline: none;
}

.form-control.is-invalid {
    border-color: #dc2626;
}

.form-actions {
    margin-top: 25px;
}

.btn-login {
    width: 100%;
    background: linear-gradient(135deg, #6366f1 0%, #4f46e5 100%);
    color: white;
    padding: 12px 25px;
    border: none;
    border-radius: 10px;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
    box-shadow: 0 4px 15px rgba(99, 102, 241, 0.3);
}

.btn-login:hover {
    transform: translateY(-2px);
    box-shadow: 0 6px 20px rgba(99, 102, 241, 0.4);
}

.btn-login:disabled {
    opacity: 0.7;
    cursor: not-allowed;
}

.error-message {
    background: linear-gradient(to right, #fee2e2, #fecaca);
    color: #991b1b;
    padding: 12px;
    border-radius: 8px;
    margin-top: 15px;
    font-size: 14px;
    text-align: center;
    box-shadow: 0 4px 15px rgba(248, 113, 113, 0.2);
}

.login-footer {
    margin-top: 25px;
    text-align: center;
    font-size: 14px;
}

.login-footer p {
    margin: 8px 0;
    color: #4a5568;
}

.login-footer a {
    color: #6366f1;
    text-decoration: none;
    font-weight: 500;
    transition: color 0.2s ease;
}

.login-footer a:hover {
    color: #4f46e5;
    text-decoration: underline;
}

.password-input-container {
    position: relative;
}

.password-toggle {
    position: absolute;
    right: 12px;
    top: 50%;
    transform: translateY(-50%);
    background: none;
    border: none;
    color: #6b7280;
    cursor: pointer;
    padding: 4px;
}

.password-toggle:hover {
    color: #4b5563;
}

@media (max-width: 480px) {
    .login-card {
        padding: 20px;
    }

    .login-title {
        font-size: 20px;
    }

    .login-subtitle {
        font-size: 14px;
    }
}
